import React, { Component } from 'react';
import './App.css';
import { Layout, message, Breadcrumb, Row, Col,Table, Avatar, Tag } from 'antd';
import { Button } from 'antd/lib/radio';

const { Header, Content, Footer } = Layout;
const mediaScheduleUrl="http://localhost:3005/api/mediaSchedule";

class App extends Component {
  constructor(props, context) {
		super(props);
		// console.log('constructor context/ PLAYER',context);
		// const userData = JSON.parse(sessionStorage.getItem('userData'));

		this.state = {
			schedule_list:[],
			// rentalInfo:{Camera_id:"", Now:{},Total:{},cost:680}
		}
	}
	componentDidMount = async () => {

		try {	
            const schedule = await fetch(`${mediaScheduleUrl}/`);
            const schedules = await schedule.json();
            if(schedules.status==="Fail") return message.error(schedules.status_msg);
            // this.bindSocketEvent(results);
            this.setState({schedule_list:schedules.schedule_list});
		} catch (error) {
			// Catch any errors for any of the above operations.
			alert(`Failed to load web3, accounts, or contract. Check console for details.`);
			console.error(error);
		}
	}

    render() {
        const columns = [
            {
                title: 'Media Name',
                dataIndex: 'media_name',
                key: 'media_name',
            }, 

            {
                title: 'Media',
                dataIndex: 'media_url',
                    render: (text, record) => ( 
                        <Avatar shape="square"   icon="picture"  src={ 'http://'+text} />
                    )
            }, 
            {
                title: 'Created Time',
                dataIndex: 'created_time',
                key: 'created_time',
                render:(text, record)=>(
                    moment(text).format("YYYY-MM-DD")
                )
            }, 
            {
                title: 'Trust',
                dataIndex: 'audited_status', 
                render:(text, record)=>{

                    let auditedInfo="Padding"	
                    if(text===0){auditedInfo="Failed"}
                    if(text===1){auditedInfo="Trusted"}
                    return (
                        <Tag>{auditedInfo}</Tag>

                    )
                }
            }, 
            {
                title: 'Media Size',
                dataIndex: 'media_size',
                key: 'media_size',
                render:(text, record)=>(
                    `${text} byte`
                )
            }, 
        ];
    return (
        <Layout>
            <Header style={{ position: 'fixed', zIndex: 1, width: '100%', height:'34px', lineHeight:"32px" }}></Header>
            <Content style={{ padding: '0 50px', marginTop: 34 }}>
                <Breadcrumb style={{ margin: '16px 0' }}>
                    <Breadcrumb.Item>Home</Breadcrumb.Item>
                    <Breadcrumb.Item>Schedule List</Breadcrumb.Item>
                </Breadcrumb>
                <Row style={{ background: '#fff', padding: 24, minHeight: 380 }}>Content
                    <Table rowKey={data => data.schedule_id} dataSource={this.state.schedule_list} columns={columns} />

                </Row>
                <Row>
                    <Button block >Play</Button>
                </Row>
                <Row>
                    <Col>
                    <Button>Fail</Button>
                    <Button>Success</Button>
                    </Col>
                </Row>
            </Content>
            <Footer style={{ textAlign: 'center' }}>
                Rental System Device ©2019 
            </Footer>
        </Layout>
    );
  }
}

export default App;


