import React, { Component } from 'react';
import './App.css';
import {Button,  Icon, Layout, message, Tooltip, Breadcrumb, Row, Col,Table, Avatar, Tag } from 'antd';
// import {  } from 'antd/lib/radio';
import moment from 'moment';
import _ from 'lodash';

const { Header, Content, Footer } = Layout;
const mediaScheduleUrl="http://localhost:3005/api/mediaSchedule";
const playlistApiUrl="http://localhost:3005/api/playlist";

class App extends Component {
  constructor(props, context) {
		super(props);
		// console.log('constructor context/ PLAYER',context);
		// const userData = JSON.parse(sessionStorage.getItem('userData'));

		this.state = {
			schedule_list:[],
			playlist:{},
			// rentalInfo:{Camera_id:"", Now:{},Total:{},cost:680}
		}
	}
	componentDidMount = async () => {

		try {	
            const scheduleRes = await fetch(`${mediaScheduleUrl}/`);
            const schedules = await scheduleRes.json();
            if(schedules.status==="Fail") return message.error(schedules.status_msg);

            const playlistRes = await fetch(`${playlistApiUrl}/byDevice/c3fcc585-abb0-4196-97b0-f2000dc7319d`);
            const playlist = await playlistRes.json();
            if(playlist.status==="Fail") return message.error(playlist.status_msg);
            _.map(schedules.schedule_list, (data)=>{
                data.medialist=playlist.medialist
            })

            this.setState({playlist:playlist});
            this.setState({schedule_list:schedules.schedule_list});




        } catch (error) {
			// Catch any errors for any of the above operations.
			alert(`Failed to load web3, accounts, or contract. Check console for details.`);
			console.error(error);
		}
	}

    render() {
        const columns = [
            {
                title: 'Schedule Name',
                dataIndex: 'media_schedule_name',
                key: 'media_schedule_name',
            }, 

            {
                title: 'Media',
                dataIndex: 'medialist',

                render: (text, record) => ( 
                    // console.log('text',text)
                    <Avatar shape="square"   icon="picture"  src={ 'http://'+text[0].media_url} />
                )
            }, 
            {
                title: 'Start Time - End Time',
                dataIndex: 'schedule_settings',
                key: 'schedule_settings',
                render:(text, record)=>{
                    console.log('text',text.duration_settings);
                    return( `${text.duration_settings.start_date} ~ ${text.duration_settings.end_date}`)
                }
            }, 
            {
                title: 'Created Time',
                dataIndex: 'created_time',
                key: 'created_time',
                render:(text, record)=>(
                    moment(text).format("YYYY-MM-DD")
                )
            }, 
        	{
				title: 'Action',
				dataIndex: 'action',
				render:(text, record)=>(
					<>
						<Tooltip title="Play">
							<Button shape="circle" onClick={() => this.showOrderDetial(record)} icon="caret-right"  />
						</Tooltip> 
					</>
				)
            },
        	{
				title: 'Result',
				// dataIndex: 'action',
				render:(text, record)=>(
					<>
						<Tooltip title="succeeded">
							<Button shape="circle" onClick={() => this.showOrderDetial(record)} icon="ordered-list"  />
						</Tooltip> 
						<Tooltip title="Failed">
							<Button shape="circle" onClick={() => this.showOrderDetial(record)} icon="ordered-list"  />
						</Tooltip> 
					</>
				)
            },

        ];
    return (
        <Layout>
            <Header style={{ position: 'fixed', zIndex: 1, width: '100%', height:'34px', lineHeight:"32px" }}></Header>
            <Content style={{ padding: '0 50px', marginTop: 34 }}>
                <Breadcrumb style={{ margin: '16px 0' }}>
                    <Breadcrumb.Item>Home</Breadcrumb.Item>
                    <Breadcrumb.Item>Schedule List</Breadcrumb.Item>
                </Breadcrumb>
                <Row style={{ background: '#fff', padding: 24, minHeight: 380 }}>Content
                    <Table rowKey={data => data.media_schedule_id} dataSource={this.state.schedule_list} columns={columns} />

                </Row>
                {/* <Row>
                    <Button block >Play</Button>
                </Row>
                <Row>
                    <Col>
                    <Button>Fail</Button>
                    <Button>Success</Button>
                    </Col>
                </Row> */}
            </Content>
            <Footer style={{ textAlign: 'center' }}>
                Rental System Device Â©2019 
            </Footer>
        </Layout>
    );
  }
}

export default App;


