import React, { Component } from 'react';
import './App.css';
import {Button,Col,  Modal, Layout, message, Tooltip, Breadcrumb, Row,   Table, Spin, notification  } from 'antd';
// import {  } from 'antd/lib/radio';
import moment from 'moment';
import _ from 'lodash';
import Web3 from "web3";
import RentalContract from "./contracts/RentalContract.json";
import PlaylistContract from "./contracts/PlaylistContract.json";

const { Header, Content, Footer } = Layout;
const mediaScheduleUrl="http://localhost:3005/api/mediaSchedule";
const playlistApiUrl="http://localhost:3005/api/playlist";
const web3 = new Web3('http://10.0.0.52:8545')

/**
 * account[0] 是system account
 * account[1] 是seller account
 * account[2] 是buyer account
 * 
 * account[4] 是player account
 * account[5] 是temp account 
 * */ 
class App extends Component {
  constructor(props, context) {
		super(props);
		// console.log('constructor context/ PLAYER',context);
		// const userData = JSON.parse(sessionStorage.getItem('userData'));

		this.state = {
			schedule_list:[],
			playlist:{},
			// rentalInfo:{Camera_id:"", Now:{},Total:{},cost:680}
		}
    }
    /**
     * 準備資料如下
     * 1- schedule
     * 2- rentalContract
     */
	componentDidMount = async () => {

		try {
            /**
             * subscribe contract event
             */
            var options = {
                fromBlock: '0x0',
                address: account[4]
            };
            var subscription = web3.eth.subscribe('RentalTask', options, function(error, result){
                if(error || result == null){
                    console.log('Error when watching incoming transactions: ', error.message);
                    return;
                }
                console.log('Got something back: ', result);
                // code continues...
            })
            subscription.on('data', function(log){ console.log(log); });

            const scheduleRes = await fetch(`${mediaScheduleUrl}/`);
            const schedules = await scheduleRes.json();
            if(schedules.status==="Fail") return message.error(schedules.status_msg);
            const schedule_list= schedules.schedule_list;
            console.log('schedules',schedule_list);

            // const playlistRes = await fetch(`${playlistApiUrl}/byDevice/c3fcc585-abb0-4196-97b0-f2000dc7319d`);
            // const playlist = await playlistRes.json();
            // if(playlist.status==="Fail") return message.error(playlist.status_msg);
            // _.map(schedules.schedule_list, (data)=>{
            //     data.medialist=playlist.medialist
            // })
            const accounts = await web3.eth.getAccounts();
 			let balance =await web3.eth.getBalance(accounts[4])
            balance = web3.utils.fromWei(balance, 'ether')

            const networkId = await web3.eth.net.getId();


            const rentalContract = new web3.eth.Contract(
				RentalContract.abi,
				RentalContract.networks[networkId].address,
            );
            rentalContract.methods.initialRental(PlaylistContract.networks[networkId].address).send({from: accounts[4], gas:200000})
            // this.setState({});

            this.setState({rentalContract:rentalContract, accounts:accounts, balance:balance, schedule_list:schedule_list});




        } catch (error) {
			// Catch any errors for any of the above operations.
			alert(`Failed to load web3, accounts, or contract. Check console for details.`);
			console.error(error);
		}
	}

    // 1 show create model
	onPlayClick = async () => {
        const {accounts} =this.state

		// await web3.eth.personal.unlockAccount("0xE733e57d488Bc4cb7FA2a51e3EA38f955104DD40", "pass1234",1000)
        // console.log('onAddPlayerClick');

        // web3.eth.sendTransaction({from: accounts[0], to: accounts[5], value: '90000000000000000000'})
        // .once('transactionHash', function(hash){ 
        //     notification.open({ message: 'PENDING', description: `Detials From:${hash.substring(0,7)}.... to ....${hash.substr(hash.length -7)}`, duration: 0,icon: <Spin  />, });
        // })
        // .once('receipt', async(receipt)=>{            
        //     let balance =await web3.eth.getBalance(accounts[4])
        //     balance = web3.utils.fromWei(balance, 'ether')
        //     this.setState({balance:balance});

        //     notification.destroy();  

        // })


		this.setState({
			showPlayModal: true,
		});
    }
    /**
     * 2 on succeeded click 
     * sent rent_fee 85
     * */ 
    onSucceededClick = async () => {
        console.log('onSucceededClick');
		// await web3.eth.personal.unlockAccount("0xE733e57d488Bc4cb7FA2a51e3EA38f955104DD40", "pass1234",1000)
        const {accounts} =this.state

        web3.eth.sendTransaction({from: accounts[5], to: accounts[4], value: '85000000000000000000'})
        .once('transactionHash', function(hash){ 
            notification.open({ message: 'PENDING', description: `Detials From:${hash.substring(0,7)}.... to ....${hash.substr(hash.length -7)}`, duration: 0,icon: <Spin  />, });
        })
        .once('receipt', async(receipt)=>{  
            let balance =await web3.eth.getBalance(accounts[4])
            balance = web3.utils.fromWei(balance, 'ether')
            this.setState({balance:balance});

            notification.destroy();
         })


        this.setState({
			showPlayModal: true,
		});
    }
    onCancelBtnClick = () => {
		console.log('onCancelBtnClick');
		this.setState({
			showPlayModal: false,
        });

	}
    /**
     * 3 on Failed click 
     * refund rent_fee 85
     * */ 
	onFailedClick = async () => {
		// await web3.eth.personal.unlockAccount("0xE733e57d488Bc4cb7FA2a51e3EA38f955104DD40", "pass1234",1000)

		// console.log('onAddPlayerClick');
		// this.setState({
		// 	showPlayModal: true,
		// });
    }
    	//Delete 
	showDeleteConfirm(value) {
		Modal.confirm({
			title:"Are you sure you want to delete this schedule?",
			okText: "OK",
			cancelText: "Cancel",
			onOk: (e) => this.yes(value),
		});
	}
	yes(value) {

	return new Promise((resolve, reject) => {

			setTimeout(Math.random() > 0.3 ? resolve : reject, 10);
			var url = `${mediaScheduleUrl}/` + value;
				fetch(url, {method: 'DELETE',})
			.then(res=>res.json())
			.then(() => {
				fetch(`${mediaScheduleUrl}/`).then(res=>res.json())
				.then((results) => {
					console.log(results);
					this.setState({schedule_list:results.schedule_list})			
				})
			})
		})
    }

    render() {
        const {contract} =this.state;
        const columns = [
            {
                title: 'Playlist Name',
                dataIndex: 'media_schedule_name',
                key: 'media_schedule_name',
            }, 
            {
                title: 'Start Time - End Time',
                dataIndex: 'schedule_settings',
                key: 'schedule_settings',
                render:(text, record)=>{
