import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import { Layout, Icon, Breadcrumb, Row, Col, Card } from 'antd';
import { Button } from 'antd/lib/radio';

const { Header, Content, Footer } = Layout;
const mediaScheduleUrl="http://localhost:3005/api/mediaSchedule";

class App extends Component {
  constructor(props, context) {
		super(props);
		console.log('constructor context/ PLAYER',context);
		const userData = JSON.parse(sessionStorage.getItem('userData'));

		this.state = {
			userData:userData, 
			showSmartContractModal: false, 
			showAddPlayerModal:false, 
			showRentalInfomModal:false, 
			device_list:[],
			currentDevice:{},
			medialist:[],
			rentalInfo:{Camera_id:"", Now:{},Total:{},cost:680}
		}
	}
	componentDidMount = async () => {

		try {	
            const devicesRes = await fetch(`${devicesApiUrl}/`);
            const devices = await devicesRes.json();
            if(devices.status==="Fail") return message.error(devices.status_msg);
            // this.bindSocketEvent(results);
            this.setState({device_list:devices.device_list});

            const mediaRes = await fetch(`${mediaApiUrl}/`);
            const media = await mediaRes.json();
            if(media.status==="Fail") return message.error(media.status_msg);
            // this.bindSocketEvent(results);
			this.setState({media_list:media.media_list});



		} catch (error) {
			// Catch any errors for any of the above operations.
			alert(
			`Failed to load web3, accounts, or contract. Check console for details.`,
			);
			console.error(error);
		}
	}

  render() {
    return (
      <Layout>
        <Header style={{ position: 'fixed', zIndex: 1, width: '100%', height:'34px', lineHeight:"32px" }}>

        <Row>
            {/* <Col span={8}>
                <Icon type="home"  style={{  color: '#fff' }} />
                <Icon type="instagram"  style={{ paddingLeft:"6px",  color: '#fff' }} />
            </Col>
            <Col offset={9} span={7}>
                <Icon type="wifi"  style={{ paddingLeft:"6px",  color: '#fff' }} />
                <Icon type="sound"  style={{ paddingLeft:"6px",  color: '#fff' }} />
                <Icon type="wechat"  style={{ paddingLeft:"6px",  color: '#fff' }} />
                <Icon type="clock-circle"  style={{ paddingLeft:"6px",  color: '#fff' }} />

            </Col> */}
        </Row>
        </Header>
        <Content style={{ padding: '0 50px', marginTop: 34 }}>
          <Breadcrumb style={{ margin: '16px 0' }}>
            <Breadcrumb.Item>Home</Breadcrumb.Item>
            <Breadcrumb.Item>Schedule List</Breadcrumb.Item>
          </Breadcrumb>
          <Row style={{ background: '#fff', padding: 24, minHeight: 380 }}>Content
            <Col>


            </Col>
          </Row>
          <Row>
               <Button block >Play</Button>
           </Row>
          <Row>
              <Col>
              <Button>Fail</Button>
              <Button>Success</Button>
              </Col>
          </Row>
        </Content>
        <Footer style={{ textAlign: 'center' }}>
          Rental System Device Â©2019 
        </Footer>
      </Layout>
    );
  }
}

export default App;


