import React, { Component } from 'react';
import './App.css';
import {Button,Col,  Modal, Layout, message, Tooltip, Breadcrumb, Row,   Table, Spin, notification  } from 'antd';
// import {  } from 'antd/lib/radio';
import moment from 'moment';
import _ from 'lodash';
import Web3 from "web3";

const { Header, Content, Footer } = Layout;
const mediaScheduleUrl="http://localhost:3005/api/mediaSchedule";
const playlistApiUrl="http://localhost:3005/api/playlist";
const web3 = new Web3('http://10.0.0.52:8545')

class App extends Component {
  constructor(props, context) {
		super(props);
		// console.log('constructor context/ PLAYER',context);
		// const userData = JSON.parse(sessionStorage.getItem('userData'));

		this.state = {
			schedule_list:[],
			playlist:{},
			// rentalInfo:{Camera_id:"", Now:{},Total:{},cost:680}
		}
	}
	componentDidMount = async () => {

		try {	
            const scheduleRes = await fetch(`${mediaScheduleUrl}/`);
            const schedules = await scheduleRes.json();
            if(schedules.status==="Fail") return message.error(schedules.status_msg);

            const playlistRes = await fetch(`${playlistApiUrl}/byDevice/c3fcc585-abb0-4196-97b0-f2000dc7319d`);
            const playlist = await playlistRes.json();
            if(playlist.status==="Fail") return message.error(playlist.status_msg);
            _.map(schedules.schedule_list, (data)=>{
                data.medialist=playlist.medialist
            })
            const accounts = await web3.eth.getAccounts();
			console.log('accounts',accounts);
			const balance =await web3.eth.getBalance(accounts[5])

            // this.setState({});
            this.setState({accounts:accounts, balance:balance, playlist:playlist, schedule_list:schedules.schedule_list});




        } catch (error) {
			// Catch any errors for any of the above operations.
			alert(`Failed to load web3, accounts, or contract. Check console for details.`);
			console.error(error);
		}
	}

    // 1 show create model
	onPlayClick = async () => {
        const {accounts} =this.state

		// await web3.eth.personal.unlockAccount("0xE733e57d488Bc4cb7FA2a51e3EA38f955104DD40", "pass1234",1000)
        // console.log('onAddPlayerClick');

        web3.eth.sendTransaction({from: accounts[0], to: accounts[5], value: '5000000000000000000'})
        .once('transactionHash', function(hash){ 
            notification.open({ message: 'PENDING', description: `Detials From:${hash.substring(0,7)}.... to ....${hash.substr(hash.length -7)}`, duration: 0,icon: <Spin  />, });
        })
        .once('receipt', function(receipt){   })
        .then(function(receipt){
            notification.destroy();
            // will be fired once the receipt its mined
        });

		this.setState({
			showPlayModal: true,
		});
    }
    /**
     * 2 on succeeded click 
     * sent rent_fee 85
     * */ 
    onSucceededClick = async () => {
        console.log('onSucceededClick');
		// await web3.eth.personal.unlockAccount("0xE733e57d488Bc4cb7FA2a51e3EA38f955104DD40", "pass1234",1000)
        const {accounts} =this.state

        web3.eth.sendTransaction({from: accounts[0], to: accounts[5], value: '85000000000000000000'})
        .once('transactionHash', function(hash){ 
            notification.open({ message: 'PENDING', description: `Detials From:${hash.substring(0,7)}.... to ....${hash.substr(hash.length -7)}`, duration: 0,icon: <Spin  />, });
        })
        .once('receipt', function(receipt){   })
        .then(function(receipt){
            notification.destroy();
            // will be fired once the receipt its mined
        });

        this.setState({
			showPlayModal: true,
		});
    }
    onCancelBtnClick = () => {
		console.log('onCancelBtnClick');
		this.setState({
			showPlayModal: false,
        });

	}
    /**
     * 3 on Failed click 
     * refund rent_fee 85
     * */ 
	onFailedClick = async () => {
		// await web3.eth.personal.unlockAccount("0xE733e57d488Bc4cb7FA2a51e3EA38f955104DD40", "pass1234",1000)

		// console.log('onAddPlayerClick');
		// this.setState({
		// 	showPlayModal: true,
		// });
    }
    	//Delete 
	showDeleteConfirm(value) {
		Modal.confirm({
			title:"Are you sure you want to delete this schedule?",
			okText: "OK",
			cancelText: "Cancel",
			onOk: (e) => this.yes(value),
		});
	}
	yes(value) {

	return new Promise((resolve, reject) => {

			setTimeout(Math.random() > 0.3 ? resolve : reject, 10);
			var url = `${mediaScheduleUrl}/` + value;
				fetch(url, {method: 'DELETE',})
			.then(res=>res.json())
			.then(() => {
				fetch(`${mediaScheduleUrl}/`).then(res=>res.json())
				.then((results) => {
					console.log(results);
					this.setState({schedule_list:results.schedule_list})			
				})
			})
		})
    }

    render() {
        const columns = [
            {
                title: 'Playlist Name',
                dataIndex: 'media_schedule_name',
                key: 'media_schedule_name',
            }, 

            // {
            //     title: 'Media',
            //     dataIndex: 'medialist',

            //     render: (text, record) => ( 
            //         // console.log('text',text)
            //         <Avatar shape="square"   icon="picture"  src={ 'http://'+text[0].media_url} />
            //     )
            // }, 
            {
                title: 'Start Time - End Time',
                dataIndex: 'schedule_settings',
                key: 'schedule_settings',
                render:(text, record)=>{
                    console.log('text',text.duration_settings);
                    return( `${text.duration_settings.start_date} ~ ${text.duration_settings.end_date}`)
                }
            }, 
            {
                title: 'Status',
                dataIndex: 'status',
                key: 'status',

            }, 
            {
                title: 'Created Time',
                dataIndex: 'created_time',
                key: 'created_time',
                render:(text, record)=>(
                    moment(text).format("YYYY-MM-DD")
                )
            }, 
        	{
				title: 'Action',
				dataIndex: 'action',
				render:(text, record)=>(
					<>
						<Tooltip title="Play">
							<Button shape="circle" onClick={() => this.onPlayClick(record)} icon="caret-right"  />
						</Tooltip> 
                        <Tooltip title="Delete">
                            <Button shape="circle" onClick={() => this.showDeleteConfirm(record.media_schedule_id)} icon="delete"  />
						</Tooltip> 
					</>
				)
            },
        	// {
			// 	title: 'Result',
			// 	// dataIndex: 'action',
			// 	render:(text, record)=>(
			// 		<>
			// 			<Tooltip title="Delete">
			// 				<Button shape="circle" onClick={() => this.onSucceededClick(record)} icon="check"  />
			// 			</Tooltip> 
			// 			<Tooltip title="Failed">
			// 				<Button shape="circle" onClick={() => this.onFailedClick(record)} icon="close"  />
			// 			</Tooltip> 
			// 		</>
			// 	)
            // },

        ];
    return (
        <Layout>
            <Header style={{ position: 'fixed', zIndex: 1, width: '100%', height:'34px', lineHeight:"32px" }}></Header>
            <Content style={{ padding: '0 50px', marginTop: 34 }}>
                <Breadcrumb style={{ margin: '16px 0' }}>
                    <Breadcrumb.Item></Breadcrumb.Item>
                    <Breadcrumb.Item>Device Name</Breadcrumb.Item>
                </Breadcrumb>
                <Row>
					<Col span={16}> 
					</Col>
					<Col span={8}>
						<div style={{margin:"8px"}}>
							<span style={{ padding:"12px", color:"red"}}>Coin: {this.state.balance} </span>
						</div>	
					</Col>
				</Row>
                <Row style={{ background: '#fff', padding: 24, minHeight: 380 }}>Content
                    <Table rowKey={data => data.media_schedule_id} dataSource={this.state.schedule_list} columns={columns} />

                </Row>
                {/* <Row>
                    <Button block >Play</Button>
                </Row>
                <Row>
                    <Col>
                    <Button>Fail</Button>
                    <Button>Success</Button>
                    </Col>
                </Row> */}
            </Content>
            <Modal okText="OK" cancelText="Cancel" title="Play Result" width={640} visible={this.state.showPlayModal} onOk={(evt) => this.onSucceededClick()} onCancel={this.onCancelBtnClick}>
				<Button onClick={()=>this.onSucceededClick()}> Succeeded</Button>
				<Button onClick={()=>this.onFailedClick()}> Failed</Button>


			</Modal>
            <Footer style={{ textAlign: 'center' }}>
