import React, { Component } from 'react';
import './App.css';
import {Button,Col,  Modal, Layout, message, Tooltip, Breadcrumb, Row,   Table, Spin, notification  } from 'antd';
// import {  } from 'antd/lib/radio';
import moment from 'moment';
import _ from 'lodash';
import Web3 from "web3";
import RentalContract from "./contracts/RentalContract.json";
import PlaylistContract from "./contracts/PlaylistContract.json";

const { Header, Content, Footer } = Layout;
const mediaScheduleUrl="http://localhost:3005/api/mediaSchedule";
const transactionLogApiUrl="http://localhost:3005/api/transactionLogs";
const playlistApiUrl="http://localhost:3005/api/playlist";
const web3 = new Web3('ws://10.0.0.52:8546')

/**
 * 
 * account[1] 是seller account
 * account[2] 是buyer account
 * account[3] 是system account
 * account[4] 是player account
 * account[5] 是temp account 
 * */ 
class App extends Component {
  constructor(props, context) {
		super(props);
		// console.log('constructor context/ PLAYER',context);
		// const userData = JSON.parse(sessionStorage.getItem('userData'));

		this.state = {
			schedule_list:[],
            playlist:{},
            currentSchedule:{}
			// rentalInfo:{Camera_id:"", Now:{},Total:{},cost:680}
		}
    }
    /**
     * 準備資料如下
     * 1- schedule
     * 2- rentalContract
     */
	componentDidMount = async () => {

		try {
            /**
             * subscribe contract event
             */
            const accounts = await web3.eth.getAccounts();
            const networkId = await web3.eth.net.getId();

            // const web3socket = new Web3(new Web3.providers.WebsocketProvider("ws://10.0.0.52:8546"));
            // const socketInstance =new web3socket.eth.Contract(
			// 	RentalContract.abi,
            //     RentalContract.networks[networkId].address,
            //     {
			// 		from: accounts[4], // default from address
			// 		gasPrice: '200000000000000', // default gas price in wei, 20 gwei in this case
            //         gasLimit: "900000000"

            //     }
            // );
            // socketInstance.events.RentalTask((err, events)=>{
            // console.log(err, events)})

            // var options = {
            //     fromBlock: '0x0',
            //     address: RentalContract.networks[networkId].address,
            // };   
            // var subscription = web3.eth.subscribe('logs', options, function(error, result){
            //     console.log('error',error);
            //     if(error || result == null){
            //         console.log('Error when watching incoming transactions: ', error.message);
            //         return;
            //     }
            //     console.log('Got something back: ', result);
            //     // code continues...
            // })
            // const subscription = web3.eth.subscribe('logs', {
            //     address: RentalContract.networks[networkId].address,
            //     topics: "RentalTask"
            // }, function(error, result){
            //     if (!error)
            //         console.log(result);
            // });

            // subscription.on('data', function(log){ console.log(log); });

            const scheduleRes = await fetch(`${mediaScheduleUrl}/`);
            const schedules = await scheduleRes.json();
            if(schedules.status==="Fail") return message.error(schedules.status_msg);
            const schedule_list= schedules.schedule_list;
            console.log('schedules',schedule_list);

            // const playlistRes = await fetch(`${playlistApiUrl}/byDevice/c3fcc585-abb0-4196-97b0-f2000dc7319d`);
            // const playlist = await playlistRes.json();
            // if(playlist.status==="Fail") return message.error(playlist.status_msg);
            // _.map(schedules.schedule_list, (data)=>{
            //     data.medialist=playlist.medialist
            // })
 			let balance =await web3.eth.getBalance(accounts[4])
            balance = web3.utils.fromWei(balance, 'ether')

            const rentalContract = new web3.eth.Contract(
				RentalContract.abi,
				RentalContract.networks[networkId].address,
            );
            rentalContract.methods.AddPlaylist(PlaylistContract.networks[networkId].address).send({from: accounts[4], gas:200000})
            // this.setState({});
            rentalContract.events.RentalTask((err, events)=>{
                console.log(err, events)}
            )
            this.setState({rentalContract:rentalContract, accounts:accounts, balance:balance, schedule_list:schedule_list});




        } catch (error) {
