pragma solidity >=0.4.21 <0.6.0;
pragma experimental ABIEncoderV2;
contract MediaContract {

    struct MediaData{
        uint256 id;
        string Name;
        string CheckSum;
        uint8 status; //Pending 2 , Failed 0, Trusted 1
    }
    uint128[] itemIds;
    mapping (uint256 => MediaData) MediaDataList;
    MediaData[] public MediaDataArray;


    function newMedia(uint256 _id, string memory _Name, string memory _CheckSum) public {
    // In a mapping, all elements are defined. They're just empty by default
    // So, just setting the id will create a new user;
        MediaDataList[_id].id = _id;
        MediaDataList[_id].Name = _Name;
        MediaDataList[_id].CheckSum = _CheckSum;
        MediaDataList[_id].status = 1;

        MediaDataArray.push(MediaDataList[_id]);
    }   

    function getMediaStatus(uint256 _id) public view returns ( uint8 ) {
        return MediaDataList[_id].status;
    }
    function getMediaInfo(uint256 _id) public view returns (MediaData memory) {
        //return ( MediaDataList[_id].id, MediaDataList[_id].Name, MediaDataList[_id].CheckSum, MediaDataList[_id].status );
        MediaData storage b1 = MediaDataList[_id];
        return b1;
    }
    function updateStatus(uint _id, uint8 _status) public {
        MediaDataList[_id].status = _status;
    }
    function CheckMediaSum(string memory _name,string memory _checksum) public view returns (bool) {
        //return ( MediaDataList[_id].id, MediaDataList[_id].Name, MediaDataList[_id].CheckSum, MediaDataList[_id].status );
        bool checksum_status = false;
        for(uint size = 0; size < MediaDataArray.length; size ++){
            if(keccak256(abi.encodePacked(MediaDataList[size].CheckSum)) == keccak256(abi.encodePacked(_checksum)) && keccak256(abi.encodePacked(MediaDataList[size].Name)) == keccak256(abi.encodePacked(_name))){
                checksum_status = true;
            }
        }
        return checksum_status;
    }
}
